import estilo from './home.module.css';
import api from "../../servicos/api";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom"; // Para link de navegação entre páginas

function Home() {

    const [filmes, setFilmes] = useState([]);

    useEffect(() => {
        async function loadFilmes() {
            const response = await api.get("movie/now_playing", {
                params: {
                    api_key: "ce2c1fc6ee322eeefa520085c8f3880a",
                    language: "pt-BR",
                    page: 1
                }
            });
            console.log(response.data.results);
            setFilmes(response.data.results);
        }
        loadFilmes();
    }, []);

    return (
        <div>
            <h1 className={estilo.titulo}>Estou na Home</h1>
            {filmes.map((filme) => (
                <div key={filme.id}>
                    <Link to={`/detalhes/${filme.id}`}>
                        <h1>{filme.title}</h1>
                    </Link>
                    <img
                        src={`https://image.tmdb.org/t/p/w600_and_h900_bestv2${filme.backdrop_path}`}
                        alt={filme.title}
                    />
                    <p>{filme.overview}</p>
                </div>
            ))}
        </div>
    );
}

export default Home;
